          for(var i =0;i<groupedData[customerId][month].length;i++){
          var rewards = 0;
          if(groupedData[customerId][month][i].price>100){
            rewards = (groupedData[customerId][month][i].price-100) * 2 + 50; 
            }
          else if(groupedData[customerId][month][i].price>50 && groupedData[customerId][month][i].price<=100){
            rewards = groupedData[customerId][month][i].price-50
            }
            console.log("state before : ",this.state)
            //rewards = groupedData[customerId][month][i].price;
          if(customerId in this.state){ 
            console.log("customerId : ", customerId);

              if(month in this.state[customerId]){

                this.setState({
                    [customerId] : {[month] : this.state[customerId][month] + rewards, total : this.state[customerId].total + rewards}
                })
                //this.state[customerId][month] =  this.state[customerId][month] + rewards;
              }
              else{
                this.setState({
                    [customerId] : {[month] : rewards, total : this.state[customerId].total + rewards}
                })  
                //this.state[customerId][month] =  rewards;
              }
          }
          else{
            this.setState({
                [customerId] : {[month] : rewards, total : rewards}

               //[customerId] : "a"
            }) 
            //this.state[customerId][month] =  rewards;  
          }

            //this.state[customerId].total = this.state[customerId].total+ rewards;
          }
        }
      }

      /*for(x in groupedData){
        if(this.state.price>100){
            this.setState({rewards : (this.state.price-100) * 2 + 50}); 
        }
        else if(this.state.price>50 && this.state.price<=100){
            this.setState({rewards : this.state.price-50});
        }
      }*/
      console.log("d" , this.state);
    }
    calculate2 = ()=>{
