import React, {Component} from 'react';
import data from './data.json';

export default class Compute extends React.Component
{
    constructor(props){
        super();
        this.state = {price : 120, rewards : 0}
    }

    groupBy = (data,key)=>{
        return data.reduce((accumulator,current)=>{
         if(current[key] in accumulator){
             accumulator[current.customerId].push(current)
         }
         else
         {
             accumulator[current[key]] = [current]
         }
         return accumulator;
        },{});
    }

    calculate = () => {
        console.log("tid : ");
      console.log(this.groupBy(data, "customerId"));
      //this.groupBy(data,"customerId");
        if(this.state.price>100){
            this.setState({rewards : (this.state.price-100) * 2 + 50}); 
        }
        else if(this.state.price>50 && this.state.price<=100){
            this.setState({rewards : this.state.price-50});
        }
    }

    render(){
        return (
        <div>
            <button onClick={this.calculate}>Get Price</button>    
            {this.state.rewards}</div>
        );
    }    
}
