import React, {Component} from 'react';
import data from './data.json';

export default class Compute2 extends React.Component
{
    constructor(props){
        super();
        this.state = {};
    }

    addUniqueMonths = ()=>{
        var months = new Set();
        for(var i =0; i<data.length; i++){
            months.add(data[i].month)
        } 
        return months;
    }

    addUniqueCustomers = ()=>{
        var customers = new Set();
        for(var i =0; i<data.length; i++){
            customers.add(data[i].customerId)
        }
        return customers;     
    }

    calculate = () => { 
      var months = Array.from(this.addUniqueMonths()).sort();
      /*console.log("months sorted : ",months.sort());  
      var m = ['b','c','a']
      var k = months;
      console.log(k.sort());*/
      var customers = Array.from(this.addUniqueCustomers());
      var result = {}; 

      for (var i=0; i<customers.length; i++){
        result[customers[i]] = []
        result[customers[i]][0] = {}
        result[customers[i]][1] = {}

        for(var j=0; j<months.length; j++){
          result[customers[i]][0][months[j]] = 0;
          result[customers[i]][1].total = 0;
        }
      }

      for(var i=0; i<data.length; i++){
        var rewards = 0;
        if(data[i].price>100){
          rewards = (data[i].price-100) * 2 + 50; 
          }
        else if(data[i].price>50 && data[i].price<=100){
          rewards = data[i].price-50
          }
        result[data[i].customerId][0][data[i].month] = result[data[i].customerId][0][data[i].month] + rewards;
        result[data[i].customerId][1].total = result[data[i].customerId][1].total + rewards
      }

      this.setState (result);
      console.log(this.state)
    }
    render(){
        return (
        <div>
            <button onClick={this.calculate}>Get Price</button>    
          </div>
        );
    }  
}
