import React, {Component} from 'react';
import data from './data.json';

export default class Compute extends React.Component
{
    constructor(props){
        super();
        this.state = {c1 : {Jan : 0, Feb : 0, Mar : 0, Total : 0},
                      c2 : {Jan : 0, Feb : 0, Mar : 0, Total : 0}  
                    }
          /*this.state = data*/;          
    }

    groupBy = (data,key)=>{
        return data.reduce((accumulator,current)=>{
         if(current[key] in accumulator){
             accumulator[current[key]].push(current)
         }
         else
         {
             accumulator[current[key]] = [current]
         }
         return accumulator;
        },{});
    }

    calculate = () => { 
      const groupedData = this.groupBy(data, "customerId");
      var customerId = ''
      for (customerId in groupedData){
        groupedData[customerId] = this.groupBy(groupedData[customerId],"month");
        console.log("obj + ", groupedData[customerId]);
      }
      console.log("final : ",groupedData);

      for(customerId in groupedData){
          var month = ''
          for(month in groupedData[customerId])
          {
          console.log("customerId : ", customerId, "month : ", customerId, "data: ", groupedData[month][customerId]);
          var rewards = 0
          if(this.state[customerId][month].price>100){
            rewards = (this.state[customerId][month].price-100) * 2 + 50; 
            }
          else if(this.state[customerId][month].price>50 && this.state[customerId][month].price<=100){
            rewards = this.state[customerId][month].price-50
            }
          if(customerId in this.state){
              if(month in this.state[customerId]){
                this.state[customerId][month] =  this.state[customerId][month] + rewards;
              }
              else{
                this.state[customerId][month] =  rewards;
              }
          }
          else{
            this.state[customerId][month] =  rewards;  
          }
            this.state[customerId].total = this.state[customerId].total+ rewards;
          }
      }

      for(x in groupedData){
        if(this.state.price>100){
            this.setState({rewards : (this.state.price-100) * 2 + 50}); 
        }
        else if(this.state.price>50 && this.state.price<=100){
            this.setState({rewards : this.state.price-50});
        }
      }
    }
    calculate2 = ()=>{
        console.log("d" , this.state.c1);
    }

    render(){
        return (
        <div>
            <button onClick={this.calculate}>Get Price</button>    
            {this.state.rewards}</div>
        );
    }

}
