import React, {Component} from 'react';
import data from './data.json';

export default class Compute extends React.Component
{
    constructor(props){
        super();
        this.state = {price : 120, rewards : 0}
    }

    groupBy = (data,key)=>{
        return data.reduce((accumulator,current)=>{
         if(current[key] in accumulator){
             accumulator[current[key]].push(current)
         }
         else
         {
             accumulator[current[key]] = [current]
         }
         return accumulator;
        },{});
    }

    groupByT = (data,key)=>{
        return data.reduce((accumulator,current)=>{
         if(current[key] in accumulator){
             accumulator[current[key]].push(current)
         }
         else
         {
             accumulator[current[key]] = [current]
         }
         return accumulator;
        },{});
    }

    calculate = () => {
        //const data = JSON.parse(data2);
        console.log("tid : ");
      const dataGroupedByCustomer = this.groupBy(data, "customerId");
      //const dataGroupedByCustomerByMonth = this.groupByT(data, "customerId", "month");
      console.log(this.groupBy(data, "dup"));
      /*console.log("g ; " +g.a.length);
      console.log("g ; " +g.size);*/
      var x = ''
      for (x in dataGroupedByCustomer){
        dataGroupedByCustomer[x] = this.groupBy(dataGroupedByCustomer[x],"month");
        console.log(dataGroupedByCustomer[x]);

      }
      //this.groupBy(data,"customerId");
        if(this.state.price>100){
            this.setState({rewards : (this.state.price-100) * 2 + 50}); 
        }
        else if(this.state.price>50 && this.state.price<=100){
            this.setState({rewards : this.state.price-50});
        }
    }

    render(){
        return (
        <div>
            <button onClick={this.calculate}>Get Price</button>    
            {this.state.rewards}</div>
        );
    }    
}
