import React, {Component} from 'react';
import data from './data.json';
import Display from './Display.js'

export default class Compute5 extends React.Component
{
    constructor(props){
        super();
        /*this.result = {};
        this.months = [];*/
        this.state = {};
    }

    addUniqueMonths = ()=>{
        var months = new Set();
        for(var i =0; i<data.length; i++){
            months.add(data[i].month)
        } 
        return months;
    }

    addUniqueCustomers = ()=>{
        var customers = new Set();
        for(var i =0; i<data.length; i++){
            customers.add(data[i].customerId)
        }
        return customers;     
    }

    calculate = () => { 
      months = Array.from(this.addUniqueMonths());
      var customers = Array.from(this.addUniqueCustomers());
      var result = {};

      for (var i=0; i<customers.length; i++){
        this.result[customers[i]] = {}  
        for(var j=0; j<this.months.length; j++){
          this.result[customers[i]][this.months[j]] = 0;
        }
        this.result[customers[i]].total = 0;

      }

      for(var i=0; i<data.length; i++){
        var rewards = 0;
        if(data[i].price>100){
          rewards = (data[i].price-100) * 2 + 50; 
          }
        else if(data[i].price>50 && data[i].price<=100){
          rewards = data[i].price-50
          }
          this.result[data[i].customerId][data[i].month] = this.result[data[i].customerId][data[i].month] + rewards;
          this.result[data[i].customerId].total = this.result[data[i].customerId].total + rewards
      }
      this.setState({result:this.result, months:this.months})
    }

    render(){
      if(Object.keys(this.result).length === 0){
        console.log("result2 null")
        return (
          <div>
              <button onClick={this.calculate}><h3>Get Rewards</h3></button> 
          </div>
        )
      }
      return(
        <Display displayData={this.state}></Display>
      ) 
      }     
}
