                console.log("month not in state: ", month, "state before : ", result);


                result[customerId].push({[month] : rewards})
                result[customerId].total = result[customerId].total + rewards;

                console.log("month not in state: ", month, "state after : ", result);

                //this.state[customerId][month] =  rewards;
              }
          }
          else{
            console.log("customerId not in state: ", customerId, "state before : ", result, "rewards", rewards, "month", month);


            result[customerId] = {[month] : rewards, total : rewards}

               //[customerId] : "a"

